    personal_details.gender = $("label:contains('Gender:')").next().first().text();
    personal_details.marrital_status = $("label:contains('Marital Status:')").next().first().text();
    personal_details.category = $("label:contains('Category:')").next().first().text();
    personal_details.physically_challenged = $("label:contains('Physically Challenged:')").next().first().text();
